mapper.select:
SELECT ai.*, aui.user_id, aui.identity FROM auto_item ai LEFT JOIN auto_user_item aui ON ai.id = aui.item_id WHERE (aui.user_id = #{userId} OR ai.id IN ( SELECT auto_user_item.item_id FROM auto_user_item WHERE auto_user_item.user_id = #{userId} ))
and ai.name like #{name}
order by ai.create_time limit #{size} offset #{startPage}
